import React from 'react'
import { DragDropContext } from 'react-beautiful-dnd'
import { set } from './data'


function App() {

  const State = (props) => {

    const state = props.state

    const style = {
      backgroundColor: `${state.color}`,
      height: '100px',
      width: `${state.length * 100}px`,
      borderRadius: '5px'
    }

    return (
      <div key={state.id} style={style}></div>
    )

  }

  const Grid = (props) => {

    let states = props.light.gridStates

    return states.map(state => {

      return <State key={state.id} state={state}/>

    })

  }

  const Light = (props) => {

    const light = props.light

    const style = {
      width: '100px',
      height: '100px',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    }

    return (
      <div style={style} key={light.id}>
        <h3>{light.name}</h3>
      </div>
    )

  }

  const Line = (props) => {

    const style = {
      display: 'flex',
      borderTop: '1px solid grey',
      borderBottom: '1px solid grey',
    }

    return (
      <div style={style} >
        <Light light={props.light}/>
        <Grid light={props.light}/>
      </div>
    )

  }

  const LineStack = () => {

    return set.map(light => {

      return <Line light={light}/>

    })


  }









  return (
    <div className="App">
    <DragDropContext>

      <LineStack></LineStack>

    </DragDropContext>

    </div>
  );
}

export default App;
