import React from 'react';
import styled from 'styled-components';
import Nav from '../Nav/Nav'
import GoogleLogo from '../resources/google-icon.svg'

let Container = styled.div`
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    width: 100vw;
    overflow-y: hidden;
    overflow-x: hidden;
`
let Form = styled.form`
    color: white;
    display: flex;
    min-width: 300px;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    border: 3px solid rgba(3,252,194,0.9);
    background-color: rgba(3,252,194,0.02);
    padding: 50px;
    border-radius: 10px;
`
let Input = styled.input`
    color: #222;
    border: 1px solid lightgrey;
    width: 15vw;
    min-width: 200px;
    font-size: 1rem;
    background-color: lightgrey;
    border: 3px solid #eee;
    padding: 10px;
    margin: 10px;
    border-radius: 3px;
    &:focus {
        outline: none;
        background-color: white;
        border: 3px solid deeppink;
    }

    &::placeholder {
        color: white;
    }
`

let LogIn = styled.button`
    color: rgb(0, 87, 66);
    margin-top: 20px;
    padding: 10px;
    font-size: 1rem;
    width: 8vw;
    min-width: 100px;
    background-color: rgba(3,252,194,1);
    border: none;
    &:focus, &:hover {
        color: white;
        outline: none;
        background-color: rgba(0,171,131,1);
    }

`

let Label = styled.h2`
    margin-bottom: 30px;
    font-weight: 300;
`

let Other = styled.p`
    margin-top: 20px;
`

let Google = styled.button`
    margin-top: 20px;
    padding: 10px;
    border-radius: 50%;
    background-color: rgba(0,0,0,0);
    border: none;
    &:focus, &:hover {
        outline: none;
        background-color: rgba(255,255,255,0.3);
    }

`

const Login = () => {

    const handleSubmit = (e) => {
        e.preventDefault();

        console.log(e.target.name)

        if(e.target.name === "google") {
            console.log("go to google");
        }


    }



    return (
        <div>
        <Nav/>
        <Container>
           <Form onSubmit={handleSubmit}>
                <Label>log in</Label>
                <Input type="email" placeholder="email or username"></Input>
                <Input type="password" placeholder="password"></Input>
                <LogIn>log in</LogIn>
                <Other>- or log in with -</Other>
                <Google name="google">
                    <img src={GoogleLogo} alt="Google"></img>
                </Google>
           </Form>
        </Container>
        </div>

    );
};

export default Login;
