import React, { useEffect, useState } from 'react';
import './StateCreator.css';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import styled from 'styled-components'
import { currentEditorState } from './data'
import { bridge } from './bridge.js';

const ViewerContainer = styled.div`
        position: absolute;
        width: 50px;
        height: 50px;
        :active {
            width: ${props => props.duration}px;
            height: 50px;
        }
        overflow: hidden;
`;


const StateCreator = (props) => {

    useEffect(() => {
        bridge.connect();
    }, [])

    let now = new Date();
    currentEditorState.id = `state--${now.getTime()}`;

    let [color, setColor] = useState({
        hue: currentEditorState.color.hue,
        saturation: currentEditorState.color.saturation,
        brightness: currentEditorState.color.brightness
    })

    let [duration, setDuration] = useState(1);

    let [hueValidationError, setHueValidationError] = useState('');

    let [satValidationError, setSatValidationError] = useState('');

    let [briValidationError, setBriValidationError] = useState('');

    let [creatorVisibility, setCreatorVisibility] = useState(false);

    const testState = () => {

        const state = {
            xy: hslToXY(color.hue, color.saturation, color.brightness),
            transitiontime: 1
        }

        console.log(state);
        bridge.send(3, state);



    }

    const hslToXY = (h,s,l) => {

        s /= 100;
        l /= 100;

        let c = (1 - Math.abs(2 * l - 1)) * s,
            x = c * (1 - Math.abs((h / 60) % 2 - 1)),
            m = l - c/2,
            r = 0,
            g = 0,
            b = 0;

        if (0 <= h && h < 60) {
            r = c; g = x; b = 0;
            } else if (60 <= h && h < 120) {
            r = x; g = c; b = 0;
            } else if (120 <= h && h < 180) {
            r = 0; g = c; b = x;
            } else if (180 <= h && h < 240) {
            r = 0; g = x; b = c;
            } else if (240 <= h && h < 300) {
            r = x; g = 0; b = c;
            } else if (300 <= h && h < 360) {
            r = c; g = 0; b = x;
            }
            r = Math.round((r + m) * 255);
            g = Math.round((g + m) * 255);
            b = Math.round((b + m) * 255);

            console.log(`r: ${r}, g: ${g}, b: ${b}`)

        return rgbToXY(r,g,b)

    }

    const rgbToXY = (r,g,b) => {

        let red = r/255;
        let green = g/255;
        let blue = b/255;

        red = (red > 0.04045) ? Math.pow((red + 0.055) / (1.0 + 0.055), 2.4) : (red / 12.92);
        green = (green > 0.04045) ? Math.pow((green + 0.055) / (1.0 + 0.055), 2.4) : (green / 12.92);
        blue = (blue > 0.04045) ? Math.pow((blue + 0.055) / (1.0 + 0.055), 2.4) : (blue / 12.92);


        let X = red * 0.649926 + green * 0.103455 + blue * 0.197109;
        let Y = red * 0.234327 + green * 0.743075 + blue * 0.022598;
        let Z = red * 0.0000000 + green * 0.053077 + blue * 1.035763;

        let x = X/(X + Y + Z);
        let y = Y/(X + Y + Z);

        x = Number(x.toFixed(4))
        y = Number(y.toFixed(4))

        console.log(x,y);

        return [0.735,0.265]

    }


    const toggleCreator = () => {
        setCreatorVisibility(!creatorVisibility);
    }

    const handleChange = (e) => {

        setHueValidationError('');
        setSatValidationError('');
        setBriValidationError('');

        if(e.target.name === "hue" && (e.target.value < 0 || e.target.value > 360)) {
            setHueValidationError('Input value must be between 0 and 360');
            return;
        } else if (e.target.name === "saturation" && (e.target.value < 0 || e.target.value > 100)) {
            setSatValidationError('Input value must be between 0 and 100');
            return;
        } else if (e.target.name === "brightness" && (e.target.value < 1 || e.target.value > 100)) {
            setBriValidationError('Input value must be between 1 and 100');
            return;
        }

        currentEditorState.color[e.target.name] = Number(e.target.value)

        setColor({
            ...color,
            [e.target.name]: Number(e.target.value)
        })


    }

    const handleDurationChange = (e) => {
        setDuration(Number(e.target.value));
        currentEditorState.length = Number(e.target.value);

    } 

    let viewerStyle = {
        backgroundColor: `hsl(${color.hue},${color.saturation}%,${color.brightness}%)`,
        borderRadius: '5px',
        margin: '0px',
        display: 'inline-block'
    }



    return (
        creatorVisibility ? 
        <div className="creator">
            <div id="close-creator" onClick={toggleCreator}>X</div>
            <Droppable droppableId="viewer" type="STATE" isDropDisabled={true}>
                {(provided,snapshot) => (
                    <ViewerContainer ref={provided.innerRef} {...provided.droppableProps} duration={duration * 100}>
                        <Draggable draggableId={`draggable-state-${now.getTime()}`} index={1} >
                        {(provided,snap)=>(
                            <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                <div style={{...viewerStyle,
                                    height: snap.isDragging ? '100px' : '50px',
                                    width: snap.isDragging ? `${duration * 100}px` : '50px'
                                }}></div>
                            </div>
                        )}
                        </Draggable>
                        {provided.placeholder}
                    </ViewerContainer>
                )}
            </Droppable>
            <div className="lightPreview">
                <button onClick={testState}>Preview</button>
            </div>
            <div className="hue edit">
                hue
                <input className="hue-slider" name="hue" type="range" min="0" max="360" value={color.hue} step="1" onChange={handleChange}></input>
                <input className="hue-number-input" name="hue" type="number" min="0" max="360" step="1" value={color.hue} onChange={handleChange}></input>
                <div className="validation-error">{hueValidationError}</div>
            </div>
            <div className="saturation edit">
                saturation
                <input className="saturation-slider" name="saturation" type="range" min="1" max="100" value={color.saturation} step="1" onChange={handleChange}></input>
                <input className="sat-number-input" name="saturation" type="number" min="0" max="100" step="1" value={color.saturation} onChange={handleChange}></input>
                <div className="validation-error">{satValidationError}</div>
            </div>
            <div className="brightness edit">
                brightness
                <input className="brightness-slider" name="brightness" type="range" min="1" max="100" value={color.brightness} step="1" onChange={handleChange}></input>
                <input className="sat-number-input" name="brightness" type="number" min="1" max="100" step="1" value={color.brightness} onChange={handleChange}></input>
                <div className="validation-error">{briValidationError}</div>
            </div>
            <div className="duration edit">
                duration
                <input className="duraton-input" name="duration-measure" type="number" min="1" placeholder="1" onChange={handleDurationChange}></input>
            </div>

        </div>
        :
        <div>
            <div id="open-creator" onClick={toggleCreator}>New State</div>
        </div>
    )
};

export default StateCreator;
