import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import Nav from '../Nav/Nav';
import { UserContext } from '../UserContext';
import { useHistory, Link } from 'react-router-dom';
import AddIcon from '../resources/add-black-18dp.svg';


const Dashboard = () => {

    let {user, setUser} = useContext(UserContext);

    let [showDialog, setShowDialog] = useState(false);

    let history = useHistory();


    if(!user.loggedIn) history.push('/auth/login');

    let SetDialog = () => {

        return <div></div>

    }



    return (
        <div>
            <Nav />
            <Page>
                <Welcome>hi, <Link to="/account/user">parishilton</Link></Welcome>
                <Header>dashboard</Header>
                <Sets> 
                    <AddSet onClick={() => {history.push('/dashboard/set')}}>
                        <img src={AddIcon} alt="New Set" width="40px"></img>
                        <Label>new set</Label>
                    </AddSet>
                </Sets>
                <Background>
                    <Modal>
                        <SetDialog />
                    </Modal>
                </Background>

            </Page>
        </div>
    );
};

export default Dashboard;

const Page = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
`

const Header = styled.h1`
    color: deeppink;
    font-weight: 300;
    letter-spacing: 1px;
    font-weight: 800;
    padding-top: 80px;
    padding-bottom: 40px;
    text-align: center;
`

const Welcome = styled.h1`
    font-weight: 300;
    padding-top: 100px;
    text-align: center;
    & > * {
        text-decoration: none;
        color: white;
        &:hover {
            color: deeppink;
        }
    }
`

let Sets = styled.div`
    width: 80vw;
    height: 60vh;
    background-color: rgba(255,255,255,0.05);
    border-radius: 30px;
    display: flex;
    justify-content: flex-start;
    flex-wrap: wrap;
`

let Label = styled.h4`
    position: absolute;
    bottom: -30px;

`

let AddSet = styled.div`
    position: relative;
    width: 150px;
    height: 100px;
    margin: 30px;
    border: 1px solid white;
    border-radius: 10px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    cursor: pointer;
    &:hover {
        background-color: rgba(255,255,255,0.2);
    }
`

let Background = styled.div`
    width: 100vw;
    height: 100vh;
    position: fixed;
    background-color: rgba(0,0,0,0.7);
    display: flex;
    justify-content: center;
    align-items: center;
`

let Modal = styled.div`
    & > * {
        width: 30vw;
        height: 60vh;
        border: 1px solid white;
        border-radius: 10px;
        background-color: #333;
    }
`
