import React from 'react';
import Nav from '../Nav/Nav';
import styled from 'styled-components';
import GoogleLogo from '../resources/google-icon.svg';
import { Link } from 'react-router-dom';

let Container = styled.div`
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    width: 100vw;
    overflow-y: hidden;
    overflow-x: hidden;
`
let Form = styled.form`
    color: white;
    display: flex;
    min-width: 300px;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background-color: rgba(255,255,255,0.1);
    padding: 50px;
    border-radius: 10px;
`
let Input = styled.input`
    color: #222;
    border: 1px solid lightgrey;
    width: 15vw;
    min-width: 200px;
    font-size: 1rem;
    background-color: lightgrey;
    border: 3px solid #eee;
    padding: 10px;
    margin: 10px;
    border-radius: 3px;
    transition: all 200ms;
    &:focus {
        outline: none;
        background-color: white;
        border: 3px solid deeppink;
    }

    &::placeholder {
        color: #888;
    }
`

let SignUpButton = styled.button`
    color: white;
    margin-top: 20px;
    padding: 10px;
    font-size: 1rem;
    width: 8vw;
    min-width: 100px;
    background-color: deeppink;
    transition: all 200ms;
    border: none;
    border-radius: 3px;
    &:focus, &:hover {
        color: white;
        outline: none;
        background-color: rgba(0,171,131,1);
    }
`

let Label = styled.h2`
    margin-bottom: 30px;
    font-weight: 300;
    font-size: 2rem;
`

let Other = styled.p`
    margin-top: 20px;
`

let Google = styled.div`
    margin-top: 20px;
    height: 50px;
    width: 50px;
    padding: 10px;
    border-radius: 50%;
    background-color: rgba(0,0,0,0);
    border: none;
    transition: all 200ms;
    &:focus, &:hover {
        outline: none;
        background-color: rgba(255,255,255,0.3);
    }
`

let LogIn = styled.p`

`

let AuthLink = styled.div`
    display: inline;
    width: 16vw;
    min-width: 200px;
    font-size: 0.9rem;
    & > * {
        color: rgba(3,252,194,1);
        font-weight: 300;
        text-decoration: none;
        margin-top: 5px;

        &:hover {
            color: #fff;
        }

    }
`

const SignUp = () => {

    return (
        <div>
        <Nav/>
        <Container>
           <Form>
                <Label>sign up</Label>
                <Input type="email" placeholder="email"></Input>
                <Input type="text" placeholder="username"></Input>
                <Input type="password" placeholder="password"></Input>
                <Input type="password" placeholder="confirm password"></Input>
                <SignUpButton>sign up</SignUpButton>
                <LogIn>already have an account? <AuthLink><Link to="/auth/login">log in</Link></AuthLink></LogIn>
                <Other>- or sign up with -</Other>
                <Google name="google">
                    <img name="google" src={GoogleLogo} alt="Google"></img>
                </Google>

           </Form>
        </Container>
        </div>
    );
};

export default SignUp;
