import React from 'react';
import styled from 'styled-components';
import Nav from '../Nav/Nav'
import GoogleLogo from '../resources/google-icon.svg'

let Container = styled.div`
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    width: 100vw;
    overflow-y: hidden;
    overflow-x: hidden;
`
let Form = styled.form`
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    border: 3px solid rgba(3,252,194,0.9);
    background-color: rgba(3,252,194,0.02);
    padding: 50px;
    border-radius: 10px;
`
let Input = styled.input`
    color: #222;
    border: 1px solid lightgrey;
    width: 15vw;
    font-size: 1rem;
    background-color: lightgrey;
    border: 3px solid #eee;
    padding: 10px;
    margin: 10px;
    border-radius: 3px;
    &:focus {
        outline: none;
        background-color: white;
        border: 3px solid deeppink;
    }

    &::placeholder {
        color: white;
    }

`

let Label = styled.h2`
    margin-bottom: 30px;
`

const Login = () => {





    return (
        <div>
        <Nav/>
        <Container>
           <Form>
                <Label>log in</Label>
                <Input type="email" placeholder="email or username"></Input>
                <Input type="password" placeholder="password"></Input>
                <p>- or log in with -</p>
                <button>go</button>

           </Form>
        </Container>
        </div>

    );
};

export default Login;
