import axios from 'axios';

let bridgeUser = "8SdmTqNxhLaIgHo28qv5JCeAjbsby0Hl7W9-jmKI";

const getBridgeIP = async () => {

  let ip = ''
  await axios.get('https://discovery.meethue.com').then(res => {
    ip = res.data[0].internalipaddress
  }).catch(err => {
    console.log(err)
  })
  return ip;
}

const connectToBridge = (ip, user) => {
  if(user) {
      axios.get(`http://${ip}/api/${user}`).then(res => {
       console.log(res);
      })
  } else {
      createUser(ip).then(res => {
        axios.get(`http://${ip}/api/${res}`).then(res => {
          console.log(res);
        })
      });
  }
}

const createUser = (ip) => {

  let request = new Promise((resolve, reject) => {

    let requestsMade = 0;

    let bridgeRequest = setInterval(()=>{
      axios.post(`http://${ip}/api`, {devicetype: "hue-stage-wep-app"})
        .then(res => {
          if (!res.data[0].error) {
            resolve(res.data[0].success.username);
            requestsMade += 1;
            clearInterval(bridgeRequest);
          } else {
            console.log(res.data[0].error)
          }
      }).catch(err => {
        console.log(err);
      })

      if(requestsMade > 15) {
        reject('Unable to create user')
        clearInterval(bridgeRequest);
      }

    }, 2000);

  })

  return request;

}

// coefficient 182.041667
// 0 && 65535 red
// 21845 green
// 43690 blue


export const bridge = {
    connect() {
        getBridgeIP().then(ip => {
            connectToBridge(ip, bridgeUser)
        })
    }
}
