import React, { useState } from 'react';
import './StateCreator.css'

const StateCreator = (props) => {


    let [state, setState] = useState({
        hue: 0,
        saturation: 100,
        brightness: 100
    })

    let [hueValidationError, setHueValidationError] = useState('')


    let [creatorVisibility, setCreatorVisibility] = useState(false);

    const toggleCreator = () => {
        setCreatorVisibility(!creatorVisibility);
    }

    const handleChange = (e) => {

        if(e.target.name === "hue" && (e.target.value < 0 || e.target.value > 360)) {
            setHueValidationError('Input value must be between 0 and 360');
            return;
        }


        setState({
            ...state,
            [e.target.name]: Number(e.target.value)
        })
    }

    const handleDurationChange = (e) => {


    } 

    let viewerStyle = {
        backgroundColor: `hsl(${state.hue},${state.saturation}%,${state.brightness}%)`,
        width: '50px',
        height: '50px',
        borderRadius: '5px',
        border: '1px solid grey'
    }


    return (
        creatorVisibility ? 
        <div className="creator">
            <div id="close-creator" onClick={toggleCreator}>X</div>
            <div style={viewerStyle}></div>
            <div className="lightPreview">Light Preview</div>
            <div className="hue edit">
                hue
                <input className="hue-slider" name="hue" type="range" min="0" max="360" value={state.hue} step="1" onChange={handleChange}></input>
                <input className="hue-number-input" name="hue" type="number" value={state.hue} step="1" onChange={handleChange}></input>
                <div className="validation-error">{hueValidationError}</div>
            </div>
            <div className="saturation edit">
                saturation
                <input className="saturation-slider" name="saturation" type="range" min="1" max="100" value={state.saturation} step="1" onChange={handleChange}></input>
            </div>
            <div className="brightness edit">
                brightness
                <input className="brightness-slider" name="brightness" type="range" min="1" max="100" value={state.brightness} step="1" onChange={handleChange}></input>
            </div>
            <div className="duration edit">
                duration (1 = 1 measure)
                <input className="duraton-input" name="duration-measure" type="number" placeholder="1" onChange={handleDurationChange}></input>
                <input className="duraton-input" name="duration-measure" type="number" placeholder="1" onChange={handleDurationChange}></input>

            </div>

        </div>
        :
        <div>
            <div id="open-creator" onClick={toggleCreator}>New State</div>
        </div>
    )
};

export default StateCreator;
