import React, { useState } from 'react';
import './StateCreator.css'

const StateCreator = (props) => {


    let [state, setState] = useState({
        hue: 0,
        saturation: 100,
        brightness: 100
    })

    let [validationError, setValidationError] = useState('')


    let [creatorVisibility, setCreatorVisibility] = useState(false);

    const toggleCreator = () => {
        setCreatorVisibility(!creatorVisibility);
    }

    const handleChange = (e) => {
        setState({
            ...state,
            [e.target.name]: Number(e.target.value)
        })
    }

    const handleDurationChange = (e) => {


        let input = e.target.value.split(' ');
        console.log(input)

        if(input.length > 2) {
            console.log('only a maximum of 2 values allowed');
        }

        input.map(value => {
            if(value.match(/[^0-9|/]/)) {
                console.log('invalid input. only numbers and / characters allowed')
            }
        })





    } 

    let viewerStyle = {
        backgroundColor: `hsl(${state.hue},${state.saturation}%,${state.brightness}%)`,
        width: '50px',
        height: '50px',
        borderRadius: '5px',
        border: '1px solid grey'
    }


    return (
        creatorVisibility ? 
        <div className="creator">
            <div id="close-creator" onClick={toggleCreator}>X</div>
            <div style={viewerStyle}></div>
            <div className="lightPreview">Light Preview</div>
            <div className="hue edit">
                hue
                <input className="hue-slider" name="hue" type="range" min="0" max="360" defaultValue="0" step="1" onChange={handleChange}></input>
            </div>
            <div className="saturation edit">
                saturation
                <input className="saturation-slider" name="saturation" type="range" min="1" max="100" defaultValue="0" step="1" onChange={handleChange}></input>
            </div>
            <div className="brightness edit">
                brightness
                <input className="brightness-slider" name="brightness" type="range" min="1" max="100" defaultValue="50" step="1" onChange={handleChange}></input>
            </div>
            <div className="duration edit">
                duration (1 = 1 measure)
                <input className="duraton-input" name="duration" type="text" placeholder="i.e. 4, 1/4" onChange={handleDurationChange}></input>
                <div className="duration-validation"></div>
            </div>

        </div>
        :
        <div>
            <div id="open-creator" onClick={toggleCreator}>New State</div>
        </div>
    )
};

export default StateCreator;
