import React, { useState } from 'react';

const StateCreator = (props) => {


    let [creatorVisibility, setCreatorVisibility] = useState(false);

    let style = {
        position: 'absolute',
        height: '100vh',
        border: '1px solid black',
        top: '0px',
        right: '0px',
        width: '20vw',
        backgroundColor: 'white',
    };

    const toggleCreator = () => {
        setCreatorVisibility(!creatorVisibility);
    }

    return (
        creatorVisibility ? 
        <div>
            <button onClick={toggleCreator}>X</button>
        </div>
        :
        <div>
            <button onClick={toggleCreator}>New State</button>
        </div>
    )
};

export default StateCreator;
