import React from 'react'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd'
import { set } from './data'


function App() {

  const State = (props) => {

    const state = props.state

    const style = {
      backgroundColor: `${state.color}`,
      height: '100px',
      width: `${state.length * 100}px`,
      borderRadius: '5px'
    }

    return (
      <div key={state.id} style={style}></div>
    )

  }

  const Grid = (props) => {

    let states = props.light.gridStates

    return states.map((state,index) => {

      return (

        <Draggable draggableId={state.id} index={index}>
          {(provided)=>(
            <State key={state.id} state={state}/>
          )}
        </Draggable>
      )

    })

  }

  const Light = (props) => {

    const light = props.light

    const style = {
      width: '100px',
      height: '100px',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      color: 'white',

    }

    return (
      <div style={style} key={light.id}>
        <h3>{light.name}</h3>
      </div>
    )

  }

  const Line = (props) => {

    const style = {
      display: 'flex',
      borderTop: '1px solid white',
      borderBottom: '1px solid white',
      backgroundColor: '#333'
    }

    const gridStyle = {
      display: 'flex',
      justifyContent: 'flex-start'
    }

    return (
      <div style={style} >
        <Light light={props.light}/>

        <Droppable droppableId={props.light.id} direction="horizontal">

        {(provided) => (
          <div style={gridStyle} ref={provided.innerRef} {...provided.droppableProps}>
            <Grid light={props.light}/>
          </div>
        )}

        </Droppable>
      </div>
    )

  }

  const LineStack = () => {

    return set.map(light => {

      return <Line key={light.name} light={light}/>

    })


  }









  return (
    <div className="App">
    <DragDropContext>

      <LineStack/>

    </DragDropContext>

    </div>
  );
}

export default App;
